<div class="contenedor">
  <div></div>
  <div
    class="fbzKiw"
    style="
      background: linear-gradient(
        90deg,
        rgb(140, 82, 255) 0%,
        rgb(52, 130, 133) 100%
      );
    "
  >
    <div>
      <div class="cabecera">
        <div class="title-cabe">
          <div class="cabecera-title">
            <h1 class="title">Grados</h1>
          </div>
          <div class="button-agg">
            <button
              class="agregar"
              data-bs-toggle="modal"
              data-bs-target="#Modal"
            >
              + Agregar
            </button>
          </div>
        </div>
      </div>
      <div class="cuerpo">
        <div class="cuerpo-cabe">
          <div class="num">
            <label> {{ datosGrados.length }} </label>
          </div>
          <div class="des">
            <label> Grados </label>
          </div>
          <div class="busqueda" >
            <label>Buscar : </label>
