<div
  class="modal fade modal-lg"
  id="Modal"
  aria-labelledby="exampleModalLabel"
  aria-hidden="true"
>
  <form [formGroup]="myForm" (ngSubmit)="guardar(myForm)">
    <div class="modal-dialog modal-dialog-centered">
      <div class="modal-content">
        <div class="modal-header">
          <h1 class="modal-title fs-5" id="exampleModalLabel">Grados</h1>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
            (click)="cancelar()"
          ></button>
        </div>
        <div class="modal-body">
          <div class="form">
            <div type="correo" class="grado">
              <select formControlName="grado" class="grado" type="number">
                <option hidden selected value="">GRADO</option>
                <option value="11">11</option>
              </select>
              <select formControlName="curso" class="curso" type="number">
                <option hidden selected value="">CURSO</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
              </select>
            </div>
            <div type="correo" class="correo">
              <input
                formControlName="profesor"
                type="number"
                class="correo"
                placeholder="PROFESOR"
              />
            </div>
          </div>
        </div>
        <div class="modal-footer">
          <button
            (click)="cancelar()"
            type="button"
            class="btn btn-secondary"
            data-bs-dismiss="modal"
          >
            Cancelar
          </button>
          <button type="submit" class="btn btn-primary">Guardar</button>
        </div>
      </div>
    </div>
  </form>
</div>

<div class="modal fade" id="deleteGrados" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Eliminar Grado</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <label>¿ Desea Eliminar el Grado {{gradoDelete}} ?</label>
      </div>
      <div *ngFor="let delet of datosDelete" class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancelar</button>
        <button (click)="eliminar(delet)" type="button" class="btn btn-danger">Eliminar</button>
      </div>
    </div>
  </div>
</div>
