import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Grados } from '../interface/grados';
import { HttpClient } from '@angular/common/http';
import { GradosServiceService } from '../services/grados-service.service';

@Component({
  selector: 'app-grados',
  templateUrl: './grados.component.html',
  styleUrls: ['./grados.component.css']
})
export class GradosComponent implements OnInit {
  myForm!: FormGroup;
  filterPost = '';
  datosGrados: Array<Grados> = [];
  data: any;
  datos: any;

  constructor(private fb: FormBuilder, private http: HttpClient, private servicioGrados: GradosServiceService) {
  }

  ngOnInit(): void {
    this.myForm = this.fb.group({
      id: new FormControl(null),
      grado: new FormControl('GRADO', [Validators.required]),
      curso: new FormControl('CURSO', [Validators.required]),
      profesor: new FormControl('', [Validators.required]),
    });

    let arrayGrados: Array<Grados> = [{
      id: 1,
      grado: 10,
      curso: 4,
      profesor: '6577989'
    }];

    this.servicioGrados.getGrados().subscribe(datos => {
      this.datosGrados = datos.data;
    });

    this.datosGrados = arrayGrados;

  }

  guardar(form: FormGroup) {
    if (form.valid) {
      if (form.value.id && form.value.id !== 0) {
        this.servicioGrados.updateGrados(form.value).subscribe(data => {
          alert("Se Actualizo el Grado con exito!")
          this.refresh(form);
          this.myForm.reset();
        });
      } else {
        this.servicioGrados.createGrados(form.value).subscribe(data => {
          alert("Se registro el Grado con exito!")
          this.refresh(form);
          this.myForm.reset();
        });
      }
    } else {
      alert('Formulario Invalido')
    }
  }

  refresh(form: FormGroup) {
    let arrayGrados: Array<Grados> = [];
    this.servicioGrados.getGrados().subscribe(datos => {
      this.datosGrados = datos.data;
    });
  }

  editar(datos: Grados) {
    this.myForm.setValue({
      id: datos.id,
      grados: datos.grado,
      curso: datos.curso,
      profesor: datos.profesor
    })
  }

}
<div class="contenedor">
  <div></div>
  <div
    class="fbzKiw"
    style="
      background: linear-gradient(
        90deg,
        rgb(140, 82, 255) 0%,
        rgb(92, 225, 230) 100%
      );
    "
  >
    <div>
      <div class="cabecera">
        <div class="title-cabe">
          <div class="cabecera-title">
            <h1 class="title">Grados</h1>
          </div>
          <div class="button-agg">
            <button class="agregar" data-bs-toggle="modal" data-bs-target="#Modal">+ Agregar</button>
          </div>
        </div>
      </div>
      <div class="cuerpo">
        <div class="cuerpo-cabe">
          <div class="num">
            <label> 8 </label>
          </div>
          <div class="des">
            <label> Grados </label>
          </div>
          <div class="busqueda" ng-model="filterPosts">
            <label>Buscar : </label>
            <input class="buscar" />
          </div>
        </div>
        <div class="table">
        <table>
            <thead >
                <tr class="table-cabecera">
                    <th> Grado </th>
                    <th> Curso </th>
                    <th> Profesor </th>
                    <th> Acciones </th>
                </tr>
            </thead>
            <tbody>
              <tr *ngFor="let item of datosGrados" >
                <th>
                  {{item.grado}}
                </th>
                <th>
                  {{item.curso}}
                </th>
                <th>
                  {{item.profesor}}
                </th>
                <th>
                  <div class="acciones">
                    <div class="accciones-editar">
                      <img class="icon-editar" src="https://img.icons8.com/?size=1x&id=ltMOGtgNYnUg&format=png" />
                    </div>
                    <div class="accciones-delete">
                      <img class="icon-delete" src="https://img.icons8.com/?size=1x&id=67884&format=png" />
                    </div>
                  </div>
                </th>
              </tr>
            </tbody>
        </table>
        </div>
      </div>
    </div>
  </div>
</div>

<div
  class="modal fade modal-lg"
  id="Modal"
  aria-labelledby="exampleModalLabel"
  aria-hidden="true"
>
<form [formGroup]="myForm" (ngSubmit)="guardar(myForm)">
  <div class="modal-dialog modal-dialog-centered">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">
          Grados
        </h1>
        <button
          type="button"
          class="btn-close"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body">
        <div class="form">
          <div type="correo" class="grado">
            <select formControlName="grado" class="grado">
              <option hidden selected> GRADO </option>
              <option value="6"> 6 </option>
              <option value="7"> 7 </option>
              <option value="8"> 8 </option>
              <option value="9"> 9 </option>
              <option value="10"> 10 </option>
              <option value="11"> 11 </option>
            </select>
            <select formControlName="curso" class="curso">
              <option hidden selected > CURSO </option>
              <option value="1"> 1 </option>
              <option value="2"> 2 </option>
              <option value="3"> 3 </option>
              <option value="4"> 4 </option>
            </select>
          </div>
          <div type="correo" class="correo">
            <input formControlName="profesor" type="number" class="correo" placeholder="PROFESOR" />
          </div>
        </div>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">
          Cancelar
        </button>
        <button type="submit" class="btn btn-primary">Guardar</button>
      </div>
    </div>
  </div>
</form>
</div>
