import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Grados } from '../interface/grados';
import { HttpClient } from '@angular/common/http';
import { GradosServiceService } from '../services/grados-service.service';
import { AnonymousSubject } from 'rxjs/internal/Subject';

@Component({
  selector: 'app-grados',
  templateUrl: './grados.component.html',
  styleUrls: ['./grados.component.css']
})
export class GradosComponent implements OnInit {
  myForm!: FormGroup;
  filterPost = '';
  datosGrados: Array<Grados> = [];
  data: any;
  datos: any;

  constructor(private fb: FormBuilder, private http: HttpClient, private servicioGrados: GradosServiceService) {
  }

  ngOnInit(): void {
    this.myForm = this.fb.group({
      id: new FormControl(null),
      grado: new FormControl('',[Validators.required]),
      curso: new FormControl('',[Validators.required]),
      profesor: new FormControl('', [Validators.required]),
    });

    let arrayGrados: Array<Grados> = [];

    this.servicioGrados.getGrados().subscribe(data => {
      this.datosGrados = data;
    });
  }

  guardar(form: FormGroup) {
    if (form.valid) {
      if (form.value.id && form.value.id !== 0) {
        this.servicioGrados.updateGrados(form.value).subscribe(data => {
          alert("Se Actualizo el Grado con exito!")
          this.refresh();
          this.myForm.reset();
        });
      } else {
        this.servicioGrados.createGrados(form.value).subscribe(data => {
          alert("Se registro el Grado con exito!")
          this.refresh();
          this.myForm.reset();
        });
      }
    } else {
      alert('Formulario Invalido')
    }
  }

  refresh() {
    let arrayGrados: Array<Grados> = [];
    this.servicioGrados.getGrados().subscribe((data) => {
      this.datosGrados = data;
    });
  }

  editar(datos: Grados) {
    this.myForm.setValue({
      id:datos.id,
      grados:datos.grado,
      curso:datos.curso,
      profesor:datos.profesor,
    });
  }

  eliminar(form : any){
      this.servicioGrados.deleteGrados(form.value).subscribe((data) =>{
        alert("El grado ha sido eliminado!");
        this.myForm.reset();
        this.refresh();
      });
  }

  cancelar(){
    this.myForm.reset();
  }
}
<div class="contenedor">
  <div></div>
  <div
    class="fbzKiw"
    style="
      background: linear-gradient(
        90deg,
        rgb(140, 82, 255) 0%,
        rgb(52, 130, 133) 100%
      );
    "
  >
    <div>
      <div class="cabecera">
        <div class="title-cabe">
          <div class="cabecera-title">
            <h1 class="title">Grados</h1>
          </div>
          <div class="button-agg">
            <button
              class="agregar"
              data-bs-toggle="modal"
              data-bs-target="#Modal"
            >
              + Agregar
            </button>
          </div>
        </div>
      </div>
      <div class="cuerpo">
        <div class="cuerpo-cabe">
          <div class="num">
            <label> {{ datosGrados.length }} </label>
          </div>
          <div class="des">
            <label> Grados </label>
          </div>
          <div class="busqueda" ng-model="filterPosts">
            <label>Buscar : </label>
            <input class="buscar" />
          </div>
        </div>
        <div class="table">
          <table>
            <thead>
              <tr class="table-cabecera">
                <th>Grado</th>
                <th>Curso</th>
                <th>Profesor</th>
                <th style="width: 100px">Acciones</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let item of datosGrados">
                <th>
                  {{ item.grado }}
                </th>
                <th>
                  {{ item.curso }}
                </th>
                <th>
                  {{ item.profesor }}
                </th>
                <th>
                  <div class="acciones">
                    <div (click)="editar(item)" class="accciones-editar">
                      <img
                        data-bs-toggle="modal"
                        data-bs-target="#Modal"
                        class="icon-editar"
                        src="https://img.icons8.com/?size=1x&id=ltMOGtgNYnUg&format=png"
                      />
                    </div>
                    <div class="accciones-delete" (click)="eliminar(item)">
                      <img
                        class="icon-delete"
                        src="https://img.icons8.com/?size=1x&id=67884&format=png"
                      />
                    </div>
                  </div>
                </th>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>

<div
  class="modal fade modal-lg"
  id="Modal"
  aria-labelledby="exampleModalLabel"
  aria-hidden="true"
>
  <form [formGroup]="myForm" (ngSubmit)="guardar(myForm)">
    <div class="modal-dialog modal-dialog-centered">
      <div class="modal-content">
        <div class="modal-header">
          <h1 class="modal-title fs-5" id="exampleModalLabel">Grados</h1>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
            (click)="cancelar()"
          ></button>
        </div>
        <div class="modal-body">
          <div class="form">
            <div type="correo" class="grado">
              <select formControlName="grado" class="grado" type="number">
                <option value="">GRADO</option>
                <option value="11">11</option>
              </select>
              <select formControlName="curso" class="curso" type="number">
                <option hidden selected value="">CURSO</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
              </select>
            </div>
            <div type="correo" class="correo">
              <input
                formControlName="profesor"
                type="number"
                class="correo"
                placeholder="PROFESOR"
              />
            </div>
          </div>
        </div>
        <div class="modal-footer">
          <button
            (click)="cancelar()"
            type="button"
            class="btn btn-secondary"
            data-bs-dismiss="modal"
          >
